    private void DeleteProfile()
    {
        try
        {
            AccountService.RemoveAccountAsync(account.ID);
            ((UserCustomAuthenticationStateProvider) AuthenticationStateProvider).Logout();
            NavigationManager.NavigateTo("/");
        }
        catch (Exception e)
        {
            errorMessage = e.Message;
        }
    }


